<script setup>
import { ref, onMounted, onBeforeUnmount } from 'vue'
import gsap from 'gsap'
import { MotionPathPlugin } from 'gsap/MotionPathPlugin'
import { ScrollTrigger } from 'gsap/ScrollTrigger'

gsap.registerPlugin(MotionPathPlugin, ScrollTrigger)

const svgRef = ref(null)
const pathRef = ref(null)
const dotRef = ref(null)
const svgWrapperRef = ref(null)

let tween = null

const createMotion = () => {
  if (tween) tween.kill()

  tween = gsap.to(dotRef.value, {
    scrollTrigger: {
      trigger: svgWrapperRef.value,
      start: 'top center',
      end: 'bottom center',
      scrub: 1,
      markers: false, // 設 true 可看觸發位置
    },
    ease: 'none',
    motionPath: {
      path: pathRef.value,
      align: pathRef.value,
      autoRotate: true,
      alignOrigin: [0.5, 0.5],
      start: 0,
      end: 0.6,
    },
  })
}

onMounted(() => {
  createMotion()
  window.addEventListener('resize', createMotion)
})

onBeforeUnmount(() => {
  window.removeEventListener('resize', createMotion)
  tween?.kill()
})
</script>

